$(function(){$("#rateit_star").rateit({min:0,max:5,step:0.5});$("#rateit_star").bind("rated",function(d){var a=$(this);var c=a.rateit("value");var b=a.data("productid");$("#re_rateit").val(c)})});$(document).ready(function(){var a=$(".created_captcha").val();var c=$(".created_rating").val();var b="";$(window).each(function(){jQuery.ajax({type:"get",url:a,success:function(d){if(d.captcha){jQuery("div.image").html(d.captcha.image);b=d.captcha.word}}})});$(".refresh").click(function(){$(".btn-rating").prop("disabled",true);jQuery.ajax({type:"get",url:a,success:function(d){if(d.captcha){jQuery("div.image").html(d.captcha.image);b=d.captcha.word}}})});$("#captcha").change(function(){if($(this).val()==b){$(".btn-rating").prop("disabled",false)}});$(".btn-rating").click(function(){var d=$(".product_id").val();if($("#re_rateit").val()!=""){var e=$("#re_rateit").val()}else{e=0}if($("#captcha").val()==b){jQuery.ajax({type:"get",url:c,data:{product_id:d,rating:e},success:function(f){if(f.isExits==true){alert("Cám ơn bạn đã đánh giá!");window.location.reload(true)}else{alert("Bạn đã đánh giá cho sản phẩm này rồi, không thể đánh giá thêm. Cảm ơn!")}}})}else{if($("#captcha").val()==""){$(".message").text("Vui Lòng Nhập Trường Này")}else{$(".message").text("Mã không đúng, vui lòng kiểm tra lại")}}})});