INSERT INTO `localtion_lang` (`id`, `localtion_id`, `title`, `description`, `content`, `language`) VALUES
(1, 1, 'Chùa Long Sơn', '', 'Ng&ocirc;i ch&ugrave;a nổi tiếng được x&acirc;y dựng năm 1738, v&agrave;o thời vua C&agrave;n Long, đời nh&agrave; Thanh v&agrave; chủ yếu l&agrave; thờ Phật Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t. Trải qua hơn 270 năm lịch sử, ng&ocirc;i ch&ugrave;a n&agrave;y đ&atilde; được tr&ugrave;ng tu nhiều lần nhưng vẫn giữ được n&eacute;t kiến tr&uacute;c ban đầu.', 'vi'),
(2, 1, 'Chua Long Son', '', 'Ng&ocirc;i ch&ugrave;a nổi tiếng được x&acirc;y dựng năm 1738, v&agrave;o thời vua C&agrave;n Long, đời nh&agrave; Thanh v&agrave; chủ yếu l&agrave; thờ Phật Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t. Trải qua hơn 270 năm lịch sử, ng&ocirc;i ch&ugrave;a n&agrave;y đ&atilde; được tr&ugrave;ng tu nhiều lần nhưng vẫn giữ được n&eacute;t kiến tr&uacute;c ban đầu.', 'en'),
(3, 2, 'Quảng trường tự do', '', 'Quảng trường Tự do l&agrave; một quảng trường c&ocirc;ng cộng bao tr&ugrave;m hơn 240.000 m&eacute;t vu&ocirc;ng ở quận Trung Ch&iacute;nh của Đ&agrave;i Bắc, Đ&agrave;i Loan. Đ&acirc;y l&agrave; nơi c&ocirc;ng ch&uacute;ng tụ họp sau khi được ho&agrave;n th&agrave;nh v&agrave;o cuối những năm 1970. T&ecirc;n gọi quảng trường gợi nhớ lại vai tr&ograve; lịch sử quan trọng trong qu&aacute; tr&igrave;nh chuyển đổi từ thế chế độc đảng sang d&acirc;n chủ hiện đại của Đ&agrave;i Loan trong thập ni&ecirc;n 1990.', 'vi'),
(4, 2, 'Quang truong tu do', '', 'Quảng trường Tự do l&agrave; một quảng trường c&ocirc;ng cộng bao tr&ugrave;m hơn 240.000 m&eacute;t vu&ocirc;ng ở quận Trung Ch&iacute;nh của Đ&agrave;i Bắc, Đ&agrave;i Loan. Đ&acirc;y l&agrave; nơi c&ocirc;ng ch&uacute;ng tụ họp sau khi được ho&agrave;n th&agrave;nh v&agrave;o cuối những năm 1970. T&ecirc;n gọi quảng trường gợi nhớ lại vai tr&ograve; lịch sử quan trọng trong qu&aacute; tr&igrave;nh chuyển đổi từ thế chế độc đảng sang d&acirc;n chủ hiện đại của Đ&agrave;i Loan trong thập ni&ecirc;n 1990.', 'en'),
(5, 3, 'Tòa tháp Taipei 101', '', 'T&ograve;a th&aacute;p Taipei 101 hay c&ograve;n được gọi l&agrave; t&ograve;a th&aacute;p Đ&agrave;i Bắc 101, nằm ở quận Xinyi, Đ&agrave;i Bắc, Đ&agrave;i Loan. T&ograve;a th&aacute;p n&agrave;y được khởi c&ocirc;ng x&acirc;y dựng v&agrave;o năm 1999 v&agrave; đ&atilde; ho&agrave;n th&agrave;nh sau 5 năm tức l&agrave; v&agrave;o năm 2004 với kinh ph&iacute; l&ecirc;n đến 1,76 tỷ USD. Sau khi ho&agrave;n tất t&ograve;a th&aacute;p n&agrave;y c&oacute; diện t&iacute;ch s&agrave;n rộng 412.500 m2, cao đến 509,2 m v&agrave; c&oacute; 101 tầng. Taipei 101 nổi tiếng l&agrave; t&ograve;a th&aacute;p cao nhất thế giới l&uacute;c bấy giờ, đến năm 2010 th&igrave; nhường vị tr&iacute; n&agrave;y lại cho t&ograve;a nh&agrave; Burj Khalifa tại Dubai.', 'vi'),
(6, 3, 'Toa Thap Taipei 101', '', 'T&ograve;a th&aacute;p Taipei 101 hay c&ograve;n được gọi l&agrave; t&ograve;a th&aacute;p Đ&agrave;i Bắc 101, nằm ở quận Xinyi, Đ&agrave;i Bắc, Đ&agrave;i Loan. T&ograve;a th&aacute;p n&agrave;y được khởi c&ocirc;ng x&acirc;y dựng v&agrave;o năm 1999 v&agrave; đ&atilde; ho&agrave;n th&agrave;nh sau 5 năm tức l&agrave; v&agrave;o năm 2004 với kinh ph&iacute; l&ecirc;n đến 1,76 tỷ USD. Sau khi ho&agrave;n tất t&ograve;a th&aacute;p n&agrave;y c&oacute; diện t&iacute;ch s&agrave;n rộng 412.500 m2, cao đến 509,2 m v&agrave; c&oacute; 101 tầng. Taipei 101 nổi tiếng l&agrave; t&ograve;a th&aacute;p cao nhất thế giới l&uacute;c bấy giờ, đến năm 2010 th&igrave; nhường vị tr&iacute; n&agrave;y lại cho t&ograve;a nh&agrave; Burj Khalifa tại Dubai.', 'en'),
(7, 4, 'Pháp Cổ Sơn', '', 'Ph&aacute;p Cổ Sơn (DDM) l&agrave; một danh lam thắng cảnh nằm tr&ecirc;n một sườn đồi gần giao lộ ở đường cao tốc Bờ biển Bắc v&agrave; Yangmingshan-Jinshan trong Sanjie, huyện Jinshan, th&agrave;nh phố New Taipei, Đ&agrave;i Loan. Ph&aacute;p Cổ Sơn được th&agrave;nh lập v&agrave;o cuối năm H&ograve;a thượng Thiền Sư Sheng-yen. Sau đ&oacute;, dưới sự dẫn dắt của Đại sư Th&aacute;nh Nghi&ecirc;m c&ugrave;ng với nỗ lực của tập thể c&aacute;c Phật tử, Ph&aacute;p Cổ Sơn đ&atilde; trở th&agrave;nh một trung t&acirc;m tu học Phật Ph&aacute;p. C&aacute;c mục ti&ecirc;u của Ph&aacute;p Cổ Sơn l&agrave; để bảo vệ đời sống tinh thần của nh&acirc;n loại, v&agrave; x&acirc;y dựng một c&otilde;i tịnh độ trong c&otilde;i Ta B&agrave; n&agrave;y.', 'vi'),
(8, 4, 'Phap Co Son', '', 'Ph&aacute;p Cổ Sơn (DDM) l&agrave; một danh lam thắng cảnh nằm tr&ecirc;n một sườn đồi gần giao lộ ở đường cao tốc Bờ biển Bắc v&agrave; Yangmingshan-Jinshan trong Sanjie, huyện Jinshan, th&agrave;nh phố New Taipei, Đ&agrave;i Loan. Ph&aacute;p Cổ Sơn được th&agrave;nh lập v&agrave;o cuối năm H&ograve;a thượng Thiền Sư Sheng-yen. Sau đ&oacute;, dưới sự dẫn dắt của Đại sư Th&aacute;nh Nghi&ecirc;m c&ugrave;ng với nỗ lực của tập thể c&aacute;c Phật tử, Ph&aacute;p Cổ Sơn đ&atilde; trở th&agrave;nh một trung t&acirc;m tu học Phật Ph&aacute;p. C&aacute;c mục ti&ecirc;u của Ph&aacute;p Cổ Sơn l&agrave; để bảo vệ đời sống tinh thần của nh&acirc;n loại, v&agrave; x&acirc;y dựng một c&otilde;i tịnh độ trong c&otilde;i Ta B&agrave; n&agrave;y.', 'en'),
(9, 5, 'Linh Thứu Sơn', '', 'Linh Thứu Sơn - đạo tr&agrave;ng V&ocirc; Sinh. Nơi đ&acirc;y T&acirc;m Đạo ph&aacute;p sư sau hơn hai năm đoạn thực tu h&agrave;nh đ&atilde; mở đạo tr&agrave;ng V&ocirc; Sinh l&agrave;m nơi hoằng dương Phật ph&aacute;p. Ng&agrave;i được biết đến l&agrave; người s&aacute;ng lập ra bảo t&agrave;ng t&ocirc;n gi&aacute;o thế giới v&agrave; nh&agrave; s&aacute;ng lập ra tổ chức Global Family for Love and Peace được biết đến tr&ecirc;n to&agrave;n cầu.', 'vi'),
(10, 5, 'Linh Thuu Son', '', 'Linh Thứu Sơn - đạo tr&agrave;ng V&ocirc; Sinh. Nơi đ&acirc;y T&acirc;m Đạo ph&aacute;p sư sau hơn hai năm đoạn thực tu h&agrave;nh đ&atilde; mở đạo tr&agrave;ng V&ocirc; Sinh l&agrave;m nơi hoằng dương Phật ph&aacute;p. Ng&agrave;i được biết đến l&agrave; người s&aacute;ng lập ra bảo t&agrave;ng t&ocirc;n gi&aacute;o thế giới v&agrave; nh&agrave; s&aacute;ng lập ra tổ chức Global Family for Love and Peace được biết đến tr&ecirc;n to&agrave;n cầu.', 'en'),
(11, 6, 'Chợ đêm Raohe', '', 'Chợ đ&ecirc;m Raohe kh&ocirc;ng chỉ l&agrave; thi&ecirc;n đường ẩm thực Trung Hoa m&agrave; nơi đ&acirc;y cũng l&agrave; một trong những địa điểm mua sắm Đ&agrave;i Bắc v&ocirc; c&ugrave;ng nổi tiếng với du kh&aacute;ch nước ngo&agrave;i. Du kh&aacute;ch đến đ&acirc;y c&oacute; thể t&igrave;m mua mọi loại h&agrave;ng h&oacute;a từ quần &aacute;o, gi&agrave;y d&eacute;p, t&uacute;i x&aacute;ch cho đến đồ điện tử, điện thoại hay c&aacute;c vật dụng h&agrave;ng ng&agrave;y. Mua qu&agrave; lưu niệm d&agrave;nh tặng bạn b&egrave; người th&acirc;n tại chợ đ&ecirc;m Raohe l&agrave; một gợi &yacute; rất tốt d&agrave;nh cho du kh&aacute;ch. Những m&oacute;n h&agrave;ng thủ c&ocirc;ng được l&agrave;m từ tre gỗ, khăn tay th&ecirc;u tinh xảo hay những m&oacute;n đồ li&ecirc;n quan đến Phật gi&aacute;o đều l&agrave; sản phẩm đặc trưng của đất nước Đ&agrave;i Loan xinh đẹp.', 'vi'),
