INSERT INTO `post_lang` (`id`, `post_id`, `title`, `description`, `content`, `language`) VALUES
(1, 1, 'Động lực và sứ mệnh', '', '<p>Trong thời đại x&atilde; hội ph&aacute;t triển như hiện nay, khi con người lu&ocirc;n bận rộn với những cạnh tranh, những lo toan cuộc sống h&agrave;ng ng&agrave;y, du lịch trở n&ecirc;n l&agrave; nhu cầu kh&ocirc;ng thể thiếu để c&acirc;n bằng cuộc sống cho mỗi c&aacute; nh&acirc;n, mỗi tập thể. Mặt kh&aacute;c, với đội ngũ hướng dẫn vi&ecirc;n v&agrave; nh&acirc;n vi&ecirc;n điều h&agrave;nh chuy&ecirc;n nghiệp, lu&ocirc;n coi kh&aacute;ch h&agrave;ng l&agrave; trung t&acirc;m, ch&uacute;ng t&ocirc;i tin chắc sẽ l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\r\n<p>Sứ mệnh của ch&uacute;ng t&ocirc;i l&agrave; trở th&agrave;nh một c&ocirc;ng ty du lịch năng động với ti&ecirc;u ch&iacute; chất lượng dịch vụ được đặt l&ecirc;n tr&ecirc;n hết.</p>', 'vi'),
(2, 1, 'Motivation and mission', '', '<p>Trong thời đại x&atilde; hội ph&aacute;t triển như hiện nay, khi con người lu&ocirc;n bận rộn với những cạnh tranh, những lo toan cuộc sống h&agrave;ng ng&agrave;y, du lịch trở n&ecirc;n l&agrave; nhu cầu kh&ocirc;ng thể thiếu để c&acirc;n bằng cuộc sống cho mỗi c&aacute; nh&acirc;n, mỗi tập thể. Mặt kh&aacute;c, với đội ngũ hướng dẫn vi&ecirc;n v&agrave; nh&acirc;n vi&ecirc;n điều h&agrave;nh chuy&ecirc;n nghiệp, lu&ocirc;n coi kh&aacute;ch h&agrave;ng l&agrave; trung t&acirc;m, ch&uacute;ng t&ocirc;i tin chắc sẽ l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\r\n<p>Sứ mệnh của ch&uacute;ng t&ocirc;i l&agrave; trở th&agrave;nh một c&ocirc;ng ty du lịch năng động với ti&ecirc;u ch&iacute; chất lượng dịch vụ được đặt l&ecirc;n tr&ecirc;n hết.</p>', 'en'),
(3, 2, 'Về công ty', '', '<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển du lịch Kim Cương</strong>&nbsp;được th&agrave;nh lập th&aacute;ng 6/2017 bởi một đội ngũ s&aacute;ng lập trẻ v&agrave; t&agrave;i năng, những th&agrave;nh vi&ecirc;n c&oacute; bề d&agrave;y kinh nghiệm trong lĩnh vực du lịch với c&aacute;c mảng tour v&agrave; dịch vụ ch&iacute;nh như:</p>\r\n<ul>\r\n<li>Tour du lịch H&agrave;nh hương</li>\r\n<li>Tour du lịch Người cao tuổi</li>\r\n<li>Tour du lịch Vip</li>\r\n<li>Tour phượt, trải nghiệm</li>\r\n<li>Tư vấn thiết kế chương tr&igrave;nh theo y&ecirc;u cầu</li>\r\n<li>Dịch vụ v&eacute; m&aacute;y bay</li>\r\n</ul>\r\nV&agrave; c&aacute;c dịch vụ lẻ kh&aacute;c (ph&ograve;ng kh&aacute;ch sạn, xe du lịch&hellip;)', 'vi'),
(4, 2, 'About Us', '', '<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển du lịch Kim Cương</strong>&nbsp;được th&agrave;nh lập th&aacute;ng 6/2017 bởi một đội ngũ s&aacute;ng lập trẻ v&agrave; t&agrave;i năng, những th&agrave;nh vi&ecirc;n c&oacute; bề d&agrave;y kinh nghiệm trong lĩnh vực du lịch với c&aacute;c mảng tour v&agrave; dịch vụ ch&iacute;nh như:</p>\r\n<ul>\r\n<li>Tour du lịch H&agrave;nh hương</li>\r\n<li>Tour du lịch Người cao tuổi</li>\r\n<li>Tour du lịch Vip</li>\r\n<li>Tour phượt, trải nghiệm</li>\r\n<li>Tư vấn thiết kế chương tr&igrave;nh theo y&ecirc;u cầu</li>\r\n<li>Dịch vụ v&eacute; m&aacute;y bay</li>\r\n</ul>\r\nV&agrave; c&aacute;c dịch vụ lẻ kh&aacute;c (ph&ograve;ng kh&aacute;ch sạn, xe du lịch&hellip;)', 'en'),
(5, 3, 'Điểm khác biệt', '', '<p>Ch&uacute;ng t&ocirc;i c&ugrave;ng phấn đấu để tạo ra sự kh&aacute;c biệt r&otilde; r&agrave;ng giữa Diamondtour v&agrave; c&aacute;c c&ocirc;ng ty kh&aacute;c ở những điểm:</p>\r\n<ul>\r\n<li>Gi&aacute; cả cạnh tranh.</li>\r\n<li>Chất lượng h&agrave;ng đầu.</li>\r\n<li>Tr&aacute;ch nhiệm, tận t&acirc;m với kh&aacute;ch h&agrave;ng.</li>\r\n</ul>', 'vi'),
(6, 3, 'Difference', '', '<p>Ch&uacute;ng t&ocirc;i c&ugrave;ng phấn đấu để tạo ra sự kh&aacute;c biệt r&otilde; r&agrave;ng giữa Diamondtour v&agrave; c&aacute;c c&ocirc;ng ty kh&aacute;c ở những điểm:</p>\r\n<ul>\r\n<li>Gi&aacute; cả cạnh tranh.</li>\r\n<li>Chất lượng h&agrave;ng đầu.</li>\r\n<li>Tr&aacute;ch nhiệm, tận t&acirc;m với kh&aacute;ch h&agrave;ng.</li>\r\n</ul>', 'en'),
(7, 4, 'Lợi ích của bạn khi lựa chọn chúng tôi', '', '<ul>\r\n<li>C&aacute;c sản phẩm tour với chất lượng dịch vụ cao, chuẩn quốc tế.</li>\r\n<li>Hiệu quả v&agrave; tiết kiệm chi ph&iacute;.</li>\r\n</ul>\r\nThoả m&atilde;n mọi nhu cầu của mọi qu&yacute; kh&aacute;ch.', 'vi'),
(8, 4, 'Your benefits when choosing us', '', '<ul>\r\n<li>C&aacute;c sản phẩm tour với chất lượng dịch vụ cao, chuẩn quốc tế.</li>\r\n<li>Hiệu quả v&agrave; tiết kiệm chi ph&iacute;.</li>\r\n</ul>\r\nThoả m&atilde;n mọi nhu cầu của mọi qu&yacute; kh&aacute;ch.', 'en'),
(9, 5, 'Thư Ngỏ', 'Kính gửi: Quý khách hàng!\r\n\r\nLời đầu tiên, Diamondtour xin gửi lời chúc sức khoẻ, lời chào trân trọng tới quý khách hàng đã, đang và sẽ sử dụng dịch vụ của chúng tôi!\r\n\r\n', '<p>C&ocirc;ng ty ch&uacute;ng t&ocirc;i được x&acirc;y dựng n&ecirc;n từ những th&agrave;nh vi&ecirc;n c&oacute; kinh nghiệm nhiều năm trong lĩnh vực du lịch, khao kh&aacute;t đem những trải nghiệm, cảm hứng chia sẻ cho người th&acirc;n, cho gia đ&igrave;nh v&agrave; xa hơn l&agrave; cho những người c&oacute; c&ugrave;ng sở th&iacute;ch đi du lịch. Ch&iacute;nh v&igrave; vậy, bằng nhiệt huyết của m&igrave;nh, ch&uacute;ng t&ocirc;i lu&ocirc;n tin rằng m&igrave;nh sẽ th&agrave;nh c&ocirc;ng trong việc mang những gi&aacute; trị, cảm hứng v&agrave; trải nghiệm du lịch đến với mọi người.</p>\r\n<p>Với mục ti&ecirc;u vươn cao v&agrave; vươn xa hơn nữa trong ng&agrave;nh du lịch, ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng mong muốn nhận được niềm tin của qu&yacute; kh&aacute;ch h&agrave;ng gửi trao cho <strong>Diamondtour</strong>, để ch&uacute;ng t&ocirc;i vinh dự được đồng h&agrave;nh tr&ecirc;n khắp c&aacute;c nẻo đường c&ugrave;ng qu&yacute; vị chi&ecirc;m b&aacute;i c&aacute;c Th&aacute;nh t&iacute;ch, thả l&ograve;ng thưởng ngoạn ti&ecirc;n cảnh chốn nh&acirc;n gian, thể nghiệm cuộc sống kh&aacute;c m&igrave;nh ở những miền đất lạ, t&igrave;m ra những gi&aacute; trị ch&acirc;n thực trong những chuyến đi&hellip; đi thật xa để biết thật nhiều, để sống tốt v&agrave; cống hiến nhiều hơn cho x&atilde; hội.</p>\r\n<p><strong><em>Với ch&uacute;ng t&ocirc;i, th&agrave;nh c&ocirc;ng kh&ocirc;ng lấy lợi nhuận l&agrave;m thước đo m&agrave; được t&iacute;nh bằng những gi&aacute; trị ch&acirc;n thực gặt h&aacute;i được sau mỗi chuyến đi của qu&yacute; kh&aacute;ch h&agrave;ng</em></strong>.</p>\r\n<p>Ch&uacute;ng t&ocirc;i rất mong được lắng nghe &yacute; kiến đ&oacute;ng g&oacute;p của qu&yacute; kh&aacute;ch h&agrave;ng để mọi dịch vụ của c&ocirc;ng ty ch&uacute;ng t&ocirc;i được ho&agrave;n thiện hơn nữa, chuy&ecirc;n nghiệp hơn nữa trong tương lai.</p>\r\n<p>&nbsp;</p>\r\n<p>Mọi &yacute; kiến đ&oacute;ng g&oacute;p cho ch&uacute;ng t&ocirc;i xin gửi về:</p>\r\n<p>Email: <a href=\"mailto:info@diamondtour.vn\">info@diamondtour.vn</a></p>\r\n<p>Phone: 02422 393 599</p>\r\n<p>Hotline: 0869 770 333</p>', 'vi'),
(10, 5, 'Open letter', 'Kính gửi: Quý khách hàng!\r\n\r\nLời đầu tiên, Diamondtour xin gửi lời chúc sức khoẻ, lời chào trân trọng tới quý khách hàng đã, đang và sẽ sử dụng dịch vụ của chúng tôi!\r\n\r\n', '<p>C&ocirc;ng ty ch&uacute;ng t&ocirc;i được x&acirc;y dựng n&ecirc;n từ những th&agrave;nh vi&ecirc;n c&oacute; kinh nghiệm nhiều năm trong lĩnh vực du lịch, khao kh&aacute;t đem những trải nghiệm, cảm hứng chia sẻ cho người th&acirc;n, cho gia đ&igrave;nh v&agrave; xa hơn l&agrave; cho những người c&oacute; c&ugrave;ng sở th&iacute;ch đi du lịch. Ch&iacute;nh v&igrave; vậy, bằng nhiệt huyết của m&igrave;nh, ch&uacute;ng t&ocirc;i lu&ocirc;n tin rằng m&igrave;nh sẽ th&agrave;nh c&ocirc;ng trong việc mang những gi&aacute; trị, cảm hứng v&agrave; trải nghiệm du lịch đến với mọi người.</p>\r\n<p>Với mục ti&ecirc;u vươn cao v&agrave; vươn xa hơn nữa trong ng&agrave;nh du lịch, ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng mong muốn nhận được niềm tin của qu&yacute; kh&aacute;ch h&agrave;ng gửi trao cho <strong>Diamondtour</strong>, để ch&uacute;ng t&ocirc;i vinh dự được đồng h&agrave;nh tr&ecirc;n khắp c&aacute;c nẻo đường c&ugrave;ng qu&yacute; vị chi&ecirc;m b&aacute;i c&aacute;c Th&aacute;nh t&iacute;ch, thả l&ograve;ng thưởng ngoạn ti&ecirc;n cảnh chốn nh&acirc;n gian, thể nghiệm cuộc sống kh&aacute;c m&igrave;nh ở những miền đất lạ, t&igrave;m ra những gi&aacute; trị ch&acirc;n thực trong những chuyến đi&hellip; đi thật xa để biết thật nhiều, để sống tốt v&agrave; cống hiến nhiều hơn cho x&atilde; hội.</p>\r\n<p><strong><em>Với ch&uacute;ng t&ocirc;i, th&agrave;nh c&ocirc;ng kh&ocirc;ng lấy lợi nhuận l&agrave;m thước đo m&agrave; được t&iacute;nh bằng những gi&aacute; trị ch&acirc;n thực gặt h&aacute;i được sau mỗi chuyến đi của qu&yacute; kh&aacute;ch h&agrave;ng</em></strong>.</p>\r\n<p>Ch&uacute;ng t&ocirc;i rất mong được lắng nghe &yacute; kiến đ&oacute;ng g&oacute;p của qu&yacute; kh&aacute;ch h&agrave;ng để mọi dịch vụ của c&ocirc;ng ty ch&uacute;ng t&ocirc;i được ho&agrave;n thiện hơn nữa, chuy&ecirc;n nghiệp hơn nữa trong tương lai.</p>\r\n<p>&nbsp;</p>\r\n<p>Mọi &yacute; kiến đ&oacute;ng g&oacute;p cho ch&uacute;ng t&ocirc;i xin gửi về:</p>\r\n<p>Email: <a href=\"mailto:info@diamondtour.vn\">info@diamondtour.vn</a></p>\r\n<p>Phone: 02422 393 599</p>\r\n<p>Hotline: 0869 770 333</p>', 'en'),
(11, 6, 'Kinh nghiệm phòng tránh phản ứng độ cao', 'Khi khám phá vùng cao nguyên hay núi cao, một điều cần phải lưu ý là bạn phải tìm hiểu kĩ về cách phòng chống phản ứng độ cao, bởi càng lên cao thì không khí càng loãng. Nếu không tìm hiểu kĩ lưỡng sẽ ảnh hưởng xấu tới hiệu quả chuyến đi của bạn. Cùng Diamondtour tìm hiểu về phản ứng độ cao để nắm rõ bí kíp phòng tránh nào!', '<p>1, Định nghĩa phản ứng độ cao</p>\r\n<p>Phản ứng độ cao l&agrave; hiện tượng thiếu oxy cấp khi bạn leo l&ecirc;n n&uacute;i cao tr&ecirc;n 3000m như đi du lịch, leo n&uacute;i. Theo nhiều nghi&ecirc;n cứu chỉ ra, 75% người leo n&uacute;i sẽ gặp phải t&igrave;nh huống n&agrave;y nhưng t&ugrave;y những mức độ kh&aacute;c nhau.</p>\r\n<p>2, Biểu hiện của phản ứng độ cao</p>\r\n<p>Hoạt động của hệ h&ocirc; hấp gặp kh&oacute; khăn do lượng oxi thấp dẫn tới c&aacute;c phản ứng như n&ocirc;n nao, đau đầu, buồn n&ocirc;n, mất cảm gi&aacute;c ngon miệng, ch&oacute;ng mặt v&agrave; mệt mỏi. Khi AMS tăng nặng, người bệnh bị mất phương hướng, ph&ugrave; n&atilde;o, ph&ugrave; phổi cấp, nếu kh&ocirc;ng cấp cứu kịp thời c&oacute; thể tử vong.</p>\r\n<p>3, C&aacute;ch ph&ograve;ng chống</p>\r\n<p>Nắm gọn 10 b&iacute; k&iacute;p sau để đương đầu với phản ứng độ cao nh&eacute;:</p>\r\n<ul>\r\n<li>Duy tr&igrave; trạng th&aacute;i t&acirc;m l&iacute; lạc quan, thoải m&aacute;i, thả lỏng cơ thể v&agrave; hạn chế kh&ocirc;ng nghĩ tới phản ứng độ cao.</li>\r\n<li><strong>Trước khi leo n&uacute;i v&agrave;i ng&agrave;y cần ch&uacute; &yacute; nghỉ ngơi, điều chỉnh cho cơ thể trở về trạng th&aacute;i b&igrave;nh thường</strong>.</li>\r\n<li>Sau khi v&agrave;o v&ugrave;ng n&uacute;i cao cần c&acirc;n bằng đi lại, nghỉ ngơi, kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều.</li>\r\n<li><strong>Kh&ocirc;ng n&ecirc;n phụ thuộc nhiều v&agrave;o lọ/ b&igrave;nh &ocirc;xi.</strong></li>\r\n<li>Đề ph&ograve;ng bị cảm.</li>\r\n<li>Chuẩn bị trước c&aacute;c loại thuốc thường d&ugrave;ng.</li>\r\n<li>Điều tiết c&aacute;c loại thức ăn, đồ uống, n&ecirc;n ăn c&aacute;c loại thực phẩm của v&ugrave;ng cao nguy&ecirc;n đ&oacute;.</li>\r\n<li>Đảm bảo cơ thể được cung cấp đủ nước.</li>\r\n<li><strong>Tu&acirc;n thủ nguy&ecirc;n tắc &ldquo;bốn kh&ocirc;ng&rdquo;: Kh&ocirc;ng vận động mạnh, gấp; kh&ocirc;ng tắm trong ng&agrave;y đầu ti&ecirc;n v&agrave;o cao nguy&ecirc;n; kh&ocirc;ng ăn no; kh&ocirc;ng n&oacute;i to.</strong></li>\r\n<li>Nếu cảm thấy cơ thể kh&ocirc;ng ổn cần kịp thời gọi b&aacute;c sĩ (b&aacute;c sĩ thường ở lu&ocirc;n tại c&aacute;c kh&aacute;ch sạn trong cao nguy&ecirc;n) hoặc cử một th&agrave;nh vi&ecirc;n trong đo&agrave;n dẫn tới bệnh viện gần đ&oacute;, hoặc gọi tới đường d&acirc;y cấp cứu tại địa phương.</li>\r\n</ul>', 'vi'),
(12, 6, 'High altitude response experience', 'Khi khám phá vùng cao nguyên hay núi cao, một điều cần phải lưu ý là bạn phải tìm hiểu kĩ về cách phòng chống phản ứng độ cao, bởi càng lên cao thì không khí càng loãng. Nếu không tìm hiểu kĩ lưỡng sẽ ảnh hưởng xấu tới hiệu quả chuyến đi của bạn. Cùng Diamondtour tìm hiểu về phản ứng độ cao để nắm rõ bí kíp phòng tránh nào!', '<p>1, Định nghĩa phản ứng độ cao</p>\r\n<p>Phản ứng độ cao l&agrave; hiện tượng thiếu oxy cấp khi bạn leo l&ecirc;n n&uacute;i cao tr&ecirc;n 3000m như đi du lịch, leo n&uacute;i. Theo nhiều nghi&ecirc;n cứu chỉ ra, 75% người leo n&uacute;i sẽ gặp phải t&igrave;nh huống n&agrave;y nhưng t&ugrave;y những mức độ kh&aacute;c nhau.</p>\r\n<p>2, Biểu hiện của phản ứng độ cao</p>\r\n<p>Hoạt động của hệ h&ocirc; hấp gặp kh&oacute; khăn do lượng oxi thấp dẫn tới c&aacute;c phản ứng như n&ocirc;n nao, đau đầu, buồn n&ocirc;n, mất cảm gi&aacute;c ngon miệng, ch&oacute;ng mặt v&agrave; mệt mỏi. Khi AMS tăng nặng, người bệnh bị mất phương hướng, ph&ugrave; n&atilde;o, ph&ugrave; phổi cấp, nếu kh&ocirc;ng cấp cứu kịp thời c&oacute; thể tử vong.</p>\r\n<p>3, C&aacute;ch ph&ograve;ng chống</p>\r\n<p>Nắm gọn 10 b&iacute; k&iacute;p sau để đương đầu với phản ứng độ cao nh&eacute;:</p>\r\n<ul>\r\n<li>Duy tr&igrave; trạng th&aacute;i t&acirc;m l&iacute; lạc quan, thoải m&aacute;i, thả lỏng cơ thể v&agrave; hạn chế kh&ocirc;ng nghĩ tới phản ứng độ cao.</li>\r\n<li><strong>Trước khi leo n&uacute;i v&agrave;i ng&agrave;y cần ch&uacute; &yacute; nghỉ ngơi, điều chỉnh cho cơ thể trở về trạng th&aacute;i b&igrave;nh thường</strong>.</li>\r\n<li>Sau khi v&agrave;o v&ugrave;ng n&uacute;i cao cần c&acirc;n bằng đi lại, nghỉ ngơi, kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều.</li>\r\n<li><strong>Kh&ocirc;ng n&ecirc;n phụ thuộc nhiều v&agrave;o lọ/ b&igrave;nh &ocirc;xi.</strong></li>\r\n<li>Đề ph&ograve;ng bị cảm.</li>\r\n<li>Chuẩn bị trước c&aacute;c loại thuốc thường d&ugrave;ng.</li>\r\n<li>Điều tiết c&aacute;c loại thức ăn, đ