INSERT INTO `product_lang` (`id`, `product_id`, `title`, `description`, `content`, `language`, `metakeywords`, `metadescription`, `datetitle`, `datecontent`, `tripnodes`, `detailsprice`) VALUES
(1, 1, 'Chương trình du xuân đầu năm Bái Đính - Trang An', '- Chương trình du lịch tâm linh tìm hiểu, vãn cảnh và hành hương tới khu danh thắng, di sản văn hóa và thiên nhiên thế giới: Bái Đính – Tràng An.\r\n\r\n- Cung cấp dịch vụ WIFI miễn phí trong chương trình.\r\n\r\n- Dịch vụ chất lượng cao.\r\n\r\n- Cung cấp dịch vụ tín ngưỡng như: sắm, biện lễ, cung văn sớ sách theo nhu cầu của quý khách (chi phí không bao gồm).', '<p>- Chương tr&igrave;nh du lịch t&acirc;m linh t&igrave;m hiểu, v&atilde;n cảnh v&agrave; h&agrave;nh hương tới khu danh thắng, di sản văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n thế giới: B&aacute;i Đ&iacute;nh &ndash; Tr&agrave;ng An.</p>\r\n<p>- Cung cấp dịch vụ WIFI miễn ph&iacute; trong chương tr&igrave;nh.</p>\r\n<p>- Dịch vụ chất lượng cao.</p>\r\n<p>- Cung cấp dịch vụ t&iacute;n ngưỡng như: sắm, biện lễ, cung văn sớ s&aacute;ch theo nhu cầu của qu&yacute; kh&aacute;ch (chi ph&iacute; kh&ocirc;ng bao gồm).</p>', 'vi', 'Chương trình du xuân đầu năm Bái Đính - Trang An', 'Chương trình du xuân đầu năm Bái Đính - Trang An', '[\"H\\u00e0 N\\u1ed9i - Ninh B\\u00ecnh - H\\u00e0 N\\u1ed9i\"]', '[\"\"]', 'Kh&ocirc;ng g&igrave; cả.', '<p><strong>&nbsp;Ph&iacute; trọn g&oacute;i cho đo&agrave;n 20 người: / KH&Aacute;CH</strong></p>\r\n<ul>\r\n<li>Ăn trọn g&oacute;i theo chương tr&igrave;nh.</li>\r\n<li>Xe: &Ocirc; t&ocirc; tiện nghi phục vụ theo chương tr&igrave;nh.</li>\r\n<li>Hướng dẫn vi&ecirc;n phục vụ suốt tuyến;</li>\r\n<li>Bảo hiểm du lịch mức tối đa 50.000.000đ/ người/ h&agrave;nh tr&igrave;nh</li>\r\n</ul>\r\n<p><strong>&nbsp;B&aacute;o gi&aacute; tr&ecirc;n kh&ocirc;ng gồm:</strong></p>\r\n<ul>\r\n<li>Đồ ăn, đồ uống (ph&aacute;t sinh theo nhu cầu c&aacute; nh&acirc;n) v&agrave; qu&agrave; lưu niệm.</li>\r\n<li>Chi ph&iacute; c&aacute; nh&acirc;n của kh&aacute;ch ngo&agrave;i chương tr&igrave;nh.</li>\r\n<li>Thuế VAT.</li>\r\n</ul>'),
